module ThueMorseSpec where

import Test.HUnit (assertEqual)
import Test.Hspec
import ThueMorse

test ns exp = do
  print $ "Looking for: " ++ show exp
  assertEqual msg exp actual
  where
    actual = isThueMorse ns
    msg = "Failed at: " ++ showArgs
    showArgs = show ns

