module TowersSpec (spec, main) where
import Towers (towerCombination)
import Test.Hspec

spec = do
  describe "towerCombination" $ do
    it "should work for the examples" $ do
      towerCombination 2 `shouldBe` 2
      towerCombination 3 `shouldBe` 6
      towerCombination 880 `shouldBe` 

main = hspec spec
